		priority,
		resources = AnyResource,
		roles = AnyRole,
		owner = false,
		action = Allow,
	) {
		this.name = name;
		this.action = action;
		this.owner = owner;
		this.priority = priority;
		this.resources = resources;
		this.roles = roles;
	}
}

